						var oEntityType = mEntityTypes[oEntitySet.type];
						var aMockedEntries = [];
						for ( var i = 0; i < 100; i++) {
							aMockedEntries.push(this._generateDataFromEntity(oEntityType, i + 1, mComplexTypes));
						}
